import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">
      <div class="center">
        <h1>Our Newsletter</h1>
        <ValidateForm
          set={{
            errorElement: "#form_error_element"
          }}
          rules={{
            validateRequired: {
              action: "input_red_border",
              notvalidated: (notFilledInputs) => {
                console.log("Not filled required inputs",notFilledInputs);
              },
            },
          }}
        >
          <form>

          <h4 style={{}} id="form_error_element">Fill all required fields</h4>

            <div class="inputbox">
              <input type="text" required />
              <span>Email</span>
            </div>
            <div class="inputbox">
              <input type="text" required />
              <span>Password</span>
            </div>
            <div class="inputbox">
              <input type="submit" value="submit" />
            </div>
          </form>
        </ValidateForm>
      </div>
    </div>
  );
}

export default App;
