import React from 'react';

interface Props {
  children: React.ReactNode;
}

function ValidateForm(props: Props) {

  const validationWrapper = document.querySelector('#_validation_wrapper');
  if (validationWrapper) {
    const firstChild = validationWrapper.children[0];
    if (firstChild) {
      console.log(firstChild);
    }
  }


  return (
    <div id='_validation_wrapper'>
      {props.children}
    </div>
  );
}

export default ValidateForm;
