import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">
      <div class="center">
        <h1>Our Newsletter</h1>
        <ValidateForm
          errorElement="#form_error_element"
          rules={{
            validateRequired: {
              message: "fill all required fields",
              action: "input_red_border",
              notvalidated: (notFilledInputs) => {
                console.log("Not filled required inputs",notFilledInputs);
              }
            },
            ValidateMinMax: {
              message : {
                min: "exceed the minimum length",
                max: "exceed the max length"
              },
              exceedsMax: ()=> {
                console.log("exceeds max");
              },
              exceedsMin: ()=> {
                console.log("exceeds min");
              }
            }
          }}
        >
          <form>

          <div style={{width:"100%",height:"1px",display:"flex",alignItems:"center",justifyContent:"center"}} >
          <h4 style={{marginTop: "-30px",color:"red"}} id="form_error_element"></h4>
          </div>

            <div style={{marginTop:"40px"}} class="inputbox">
              <input type="text" required />
              <span>Email</span>
            </div>
            <div class="inputbox">
              <input min={4} max={10} type="text" required />
              <span>Password</span>
            </div>
            <div class="inputbox">
              <input type="submit" value="submit" />
            </div>
          </form>
        </ValidateForm>
      </div>
    </div>
  );
}

export default App;
