import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">


          <ValidateForm
            rules={{
              validateRequired: {
                action: "input",
                notvalidated:(notFilledInputs)=> {
                    console.log(notFilledInputs);
                }
              },
            }}
          >
            <form className="card-form">
              <div className="input">
                <input
                  type="text"
                  className="input-field"
                   name="full_name"
                  required
                />
                <label className="input-label">Full name</label>
              </div>
              <div className="input">
                <input
                  type="text"
                  className="input-field"
                   name="email"
                  required
                />
                <label className="input-label">Email</label>
              </div>
              <div className="input">
                <input name="password" type="password" className="input-field" required />
                <label className="input-label">Password</label>
              </div>
              <div className="action">
                <button type="submit" className="action-button">Get started</button>
              </div>
            </form>
          </ValidateForm>

    </div>
  );
}

export default App;
