import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">


          <ValidateForm
            rules={{
              validateRequired: {
                action: "input_red_border",
                notvalidated:(notFilledInputs)=> {
                    console.log(notFilledInputs);
                }
              },
            }}
          >

          </ValidateForm>
          <div class="center">
  <h1>Our Newsletter</h1>
  <form>
    <div class="inputbox">
      <input type="text" required />
      <span>Email</span>
    </div>
    <div class="inputbox">
      <input type="text" required />
      <span>Password</span>
    </div>
    <div class="inputbox">
      <input type="button" value="submit" />
    </div>
  </form>
</div>

    </div>
  );
}

