import React, { useEffect, useState } from "react";

interface Props {
  children: React.ReactNode;
}

function ValidateForm(props: Props) {
  // Wait until the document has loaded
  document.addEventListener("DOMContentLoaded", () => {
    // Get the first child of the element with the id "_validation_wrapper"
    const form = document.querySelector("#_validation_wrapper :first-child");

    // Check if the element is null
    if (form === null) {
      console.log("Element not found");
      return;
    }


    console.log(form);
  });

  return <div id="_validation_wrapper">{props.children}</div>;
}

export default ValidateForm;
