import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">
      <div className="center">
        <h1>Our Newsletter</h1>
        <ValidateForm
          onSubmit={(event)=> {
            console.log("Form submitted",event);
          }}
          errorElement="#form_error_element"
          rules={{
            ValidateCreditCard:{
              allowedCards:["Visa","Mastercard","Discover"],
              cardNumber: "card-number",
              cvv: "cvv",
              expirationDate: "expire-date",
              billingZip: "zip",
              when:"onblur",
              getCardType:(cardType)=> {
                console.log(cardType);
              },
              customErrorMessages:{
              }
            }
          }}
        >
          <form>
            <div
              style={{
                width: "100%",
                height: "1px",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <h4
                style={{ marginTop: "-30px", color: "red" }}
                id="form_error_element"
              >
                {""}
              </h4>
            </div>

            <div style={{ marginTop: "40px" }} className="inputbox">
              <input name="card-number" type="number" required />
            </div>
            <div className="inputbox">
              <input name="cvv" type="number" />
              <span>Cvv</span>
            </div>
            <div className="inputbox">
              <input name="expire-date" type="text" />
              <span>Expiration date</span>
            </div>
            <div className="inputbox">
              <input name="zip" type="text" />
              <span>Zip</span>
            </div>
            <div className="inputbox">
              <input type="submit" value="submit" />
            </div>
          </form>
        </ValidateForm>
      </div>
    </div>
  );
}

export default App;
