import React, { useEffect } from "react";

interface Props {
  children: React.ReactNode;
}

const ValidateForm: React.FC = (props: Props) => {
  useEffect(() => {
    const validationWrapper = document.querySelector("#_validation_wrapper");
    const form = validationWrapper?.children[0];
  }, [props.children]);

  return <div id="_validation_wrapper">{props.children}</div>;
};

export default ValidateForm;
