import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">
      <div className="container">
        <div className="card">

          <ValidateForm
            rules={{
              validateRequired: {
                applyOnly: ["full_name","password"],
                action: "input_red_border",
                notvalidated:(res)=> {
                  if(res.status){
                    // form.submit()
                  }else{
                    console.log(res.notFilledInputs);
                  }
                }
              },
            }}
          >
            <form className="card-form">
              <div className="input">
                <input
                  type="text"
                  className="input-field"
                   name="full_name"
                  required
                />
                <label className="input-label">Full name</label>
              </div>
              <div className="input">
                <input
                  type="text"
                  className="input-field"
                   name="email"
                  required
                />
                <label className="input-label">Email</label>
              </div>
              <div className="input">
                <input name="password" type="password" className="input-field" required />
                <label className="input-label">Password</label>
              </div>
              <div className="action">
                <button type="submit" className="action-button">Get started</button>
              </div>
            </form>
          </ValidateForm>
          <div className="card-info">
            <p>
              By signing up you are agreeing to our{" "}
              <a href="#">Terms and Conditions</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
