import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">



          <div class="center">
  <h1>Our Newsletter</h1>
  <ValidateForm
            rules={{
              validateRequired: {
                action: "input_red_border",
                notvalidated:(notFilledInputs)=> {
                    console.log(notFilledInputs);
                }
              },
            }}
          >
            <form>
    <div class="inputbox">
      <input type="text" required />
      <span>Email</span>
    </div>
    <div class="inputbox">
      <input type="text" required />
      <span>Password</span>
    </div>
    <div class="inputbox">
      <input type="button" value="submit" />
    </div>
  </form>
          </ValidateForm>

</div>

    </div>
  );
}

export default App;
