import "./App.css";
import ValidateForm from "form-validation-react";

function App() {
  return (
    <div className="App">
      <div className="center">
        <h1>Our Newsletter</h1>
        <ValidateForm
          errorElement="#form_error_element"
          rules={{
            ValidateEmail:{
              emailInput:"email",
              when:"onblur",
              message:"Please enter valid yahoo email",
              type:"yahoo"
            }
          }}
        >
          <form>

          <div style={{width:"100%",height:"1px",display:"flex",alignItems:"center",justifyContent:"center"}} >
          <h4 style={{marginTop: "-30px",color:"red"}} id="form_error_element">{""}</h4>
          </div>

            <div style={{marginTop:"40px"}} className="inputbox">
              <input name="email" type="email" required />
              <span>Email</span>
            </div>
            <div className="inputbox">
              <input name="password" min={4} max={8}  required />
              <span>Password</span>
            </div>
            <div className="inputbox">
              <input type="submit" value="submit" />
            </div>
          </form>
        </ValidateForm>
      </div>
    </div>
  );
}

export default App;
